import React, { PureComponent } from 'react';
import './App.css';
import ToDoList from './components/ToDoList';
import Header from './components/Header';
import Footer from './components/Footer';
class App extends PureComponent {
  state = {
    todoList: [
      {
      id: 1,
      text: 'todo 1',
      isCompleted: true
    },
    {
      id: 2,
      text: 'todo 2',
      isCompleted: false
    }
  ],
  todoEditingId: ''
  }

  addTodo = (todo = {}) => {
    this.setState(preState => ({
      todoList: [...preState.todoList, todo]
    }))
  }
  getTodoEditingId = (id = '') => {
    this.setState({
      todoEditingId: id
    })
  } 

  onEditTodo = (todo = {}, index = -1) => {
    if(index >=0 ) {
      const { todoList: list } = this.state
      list.splice(index, 1, todo)
      this.setState({
        todoList: list,
        todoEditingId: ''
      })
    }
  }

  markCompleted = ( id ='') =>{
    this.setState(preState => ({
      todoList: preState.todoList.map(todo => todo.id === id ? ({...todo, isCompleted: !todo.isCompleted}) : todo )
    }))
  }

  render (){
    const { todoList, todoEditingId } = this.state
    return (
    <div className="App">
      <section>
      <Header addTodo = {this.addTodo}/>
      <ToDoList 
        todoList={todoList}
        getTodoEditingId = {this.getTodoEditingId}
        todoEditingId = {todoEditingId}
        onEditTodo = {this.onEditTodo}
        markComplete = {this.markCompleted}
      />
      <Footer />
      </section>
    </div>
  );
}
}

export default App;
