import React, { memo } from 'react'

const Footer = memo(props => {
    const filterBtns = [{
        title: 'All',
        isActive: true,
        onClick: () => {},
        link: ''
    },
    {
        title: 'Active',
        isActive: false,
        onClick: () => {},
        link: '/active'
    },
    {
        title: 'Completed',
        isActive: false,
        onClick: () => {},
        link: '/Completed'
    }
]
    return(
         <footer className="footer">
             <span className="todo-count">
                 <strong>2</strong>
                 <span></span>
                 <span>item</span>
                 <span>left</span>
             </span>
             <ul className="filter">
                 {
                     filterBtns.map(btn => (
                         <FilterBtn {...btn} />
                     ))
                 }
                <FilterBtn />
                <FilterBtn />
                <FilterBtn />
             </ul>

         </footer>
    )
})

const FilterBtn = memo(props => {
    return (
        <>
        <li>
            <a href="#/"
                className="selected"
                onClick= {() => {}}
            >
            All
            </a>
        </li>
        </>
    )
})

export default Footer;
